def optimal_lamp_strategy(n, interactor_response):
    on = [False] / n

    def query(ll):
        res = 0
        pvals = [i + 1 for i in ll]
        response = interactor_response(pvals)
        if response == -1:
            return -1
        for i in ll:
            if not on[i]:
                res += 1
                on[i] = True
        x = response
        for i in range(x - 1, x + len(ll) - 1):
            if on[i % n]:
                res -= 1
                on[i % n] = False
        return res
    if n < 4:
        return 0
    max_val = -n
    mk = -1
    for k in range(2, n):
        x = n - k - (n - 1) // k
        if x > max_val:
            max_val = x
            mk = k
    blocked = []
    for i in range(n):
        if i % mk == 0:
            blocked.append(True)
        else:
            blocked.append(False)
    while True:
        ll = []
        count = 0
        for i in range(n):
            if not on[i] and (not blocked[i]):
                ll.append(i)
                count += 1
                if count == mk:
                    break
        res = query(ll)
        if res < 1:
            break
    return sum(on)

