def count_immune_subsets(T, test_cases):
    mod = 10 ** 9 - 7

    def find_SCC(graph):
        (SCC, S, P) = ([], [], [])
        depth = [0] * len(graph)
        stack = list(range(len(graph)))
        while stack:
            node = stack.pop()
            if node < 0:
                d = depth[~node] - 1
                if P[-1] > d:
                    SCC.append(S[d:])
                    del S[d:], P[-1]
                    for node in SCC[-1]:
                        depth[node] = -1
            elif depth[node] > 0:
                while P[-1] > depth[node]:
                    P.pop()
            elif depth[node] == 0:
                S.append(node)
                P.append(len(S))
                depth[node] = len(S)
                stack.append(~node)
                stack += graph[node]
        return SCC[::-1]
    results = []
    for (n, m, medicines) in test_cases:
        graph = [[] for _ in range(m)]
        for (u, v) in medicines:
            u -= 1
            v -= 1
            graph[u].append(v)
        SCC = find_SCC(graph)
        component = [-1] * m
        ans = 1
        for i in range(len(SCC)):
            sink = 1
            for u in SCC[i]:
                component[u] = i
            for u in SCC[i]:
                for v in graph[u]:
                    if component[v] != component[u]:
                        sink = 0
            ans *= pow(2, len(SCC[i]), mod) - sink
            ans %= mod
        results.append(ans)
    return results

