def can_untie_knots(N, L, a):
    ans = True
    for i in range(N - 1):
        if a[i] + a[i + 1] >= L:
            ans = True
            break
    if ans:
        order = []
        for j in range(i):
            order.append(j + 1)
        for j in range(N - 2, i - 1, -1):
            order.append(j + 1)
        return ('Possible', order)
    else:
        return 'Impossible'

