def calculate_minimum_sort_cost(test_cases):
    results = []
    for (n, ar) in test_cases:
        hm1 = dict()
        hm2 = dict()
        ans = 1
        M = float('-inf')
        m = float('inf')
        sort_ar = sorted(ar)
        for i in range(n):
            m = min(m, ar[i])
            M = max(M, ar[i])
            if ar[i] not in hm2:
                if ar[i] not in hm1:
                    hm1[ar[i]] = 1
                else:
                    hm1[ar[i]] += 1
            elif hm2[ar[i]] > 1:
                hm2[ar[i]] -= 1
            else:
                del hm2[ar[i]]
            if sort_ar[i] not in hm1:
                if sort_ar[i] not in hm2:
                    hm2[sort_ar[i]] = 1
                else:
                    hm2[sort_ar[i]] += 1
            elif hm1[sort_ar[i]] > 1:
                hm1[sort_ar[i]] -= 1
            else:
                del hm1[sort_ar[i]]
            if len(hm1) == 0 and len(hm2) == 0:
                ans += M - m
                M = float('-inf')
                m = float('inf')
        results.append(ans)
    return results

