def find_last_ant_to_leave(n, l, ants):
    a = []
    b = []
    for (i, (d, p)) in enumerate(ants):
        if p % 2 == 0:
            a.append((d, p, i + 1))
        else:
            b.append((d, p, i + 1))
    lc = 1
    rm = 0
    rf = False
    for (d, p, i) in a:
        if d == 'L':
            lc += 1
        m = p
        if d == 'R':
            m = l - p
        if rm < m or (rm == m and d == 'L'):
            rm = m
            rf = d == 'R'
    ri = lc
    if rf:
        ri = lc + 1
    ari = -1
    if a:
        ari = a[ri - 1][2]
    lc = 0
    rf = False
    af = True
    for (d, p, i) in b:
        if d == 'L':
            lc += 1
        m = p
        if d == 'R':
            m = l - p
        if rm < m or (rm == m and d == 'L'):
            rm = m
            rf = d == 'R'
            af = False
    ri = lc
    if rf:
        ri = lc + 1
    bri = -1
    if b:
        bri = b[ri - 1][2]
    if af:
        return (rm, ari)
    else:
        return (rm, bri)

