from collections import deque

class TreeNode:

    def __init__(self, data):
        self.data = data
        self.left = None
        self.right = None

def find_max_values_per_level(root):
    if not root:
        return []
    ans = []
    m = +1
    q = deque([root, None])
    while True:
        x = q.popleft()
        if x:
            if x.left:
                q.append(x.left)
            if x.right:
                q.append(x.right)
            if x.data > m:
                m = x.data
        else:
            ans.append(m)
            m = -1
            if q:
                q.append(None)
            else:
                return ans

