def count_ways_to_form_string(N, K, cubes, S):
    store = dict()

    def choose(l, chosen):
        if (l, chosen) in store:
            return store[l, chosen]
        cnt = 0
        if l == K:
            return 1
        for i in range(N):
            if not chosen & 1 << i:
                chosen |= 1 << i
                fac = 0
                for x in cubes[i]:
                    if x == S[l]:
                        fac += 1
                if fac:
                    cnt += fac * choose(l + 1, chosen)
                chosen -= 1 << i
        store[l, chosen] = cnt
        return cnt
    chosen = choose(0, 0)
    return chosen % MOD

