def find_permutations_for_condition(test_cases):
    results = []
    for (n, l) in test_cases:
        f = {}
        for i in l:
            try:
                f[i] += 1
            except:
                f[i] = 1
        if max(f.values()) > n // 2 and len(set(l)) <= 2:
            results.append('NO')
        else:
            l.sort()
            B = l
            C = l[n // 2:] + l[:n // 2]
            results.append(('YES', B, C))
    return results

