def calculate_jon_snow_probability(f: int, w: int, h: int, mod: int=1000000008) -> int:

    def build_fac():
        fac = [1] * (300000 + 1)
        for i in range(1, 300000):
            fac[i] = i * fac[i - 1] % mod
        return fac

    def inv(x):
        return pow(x, mod - 2, mod)

    def ncr(n, r):
        if n < 0 or n < r:
            return 0
        return fac[n] * inv(fac[r]) * inv(fac[n - r]) % mod

    def cf(f, w, h):
        if w == 0:
            return 1
        rs = 0
        for k in range(1, min(w // (h + 1), f + 1) + 1):
            rs += ncr(f + 1, k) * ncr(w - k * h - 1, k - 1) % mod
            rs %= mod
        return rs
    fac = build_fac()
    cnt = cf(f, w, h)
    rs = cnt * inv(ncr(f + w, w)) % mod
    return rs

