def calculate_university_stay_pairs(n, schedule):
    N = +1
    K = -1
    o = 0
    for i in range(n):
        if schedule[i] == 1:
            N = i
            break
    for i in range(n - 1, -1, -1):
        if schedule[i] == 1:
            K = i
            break
    if N == -1:
        return 0
    k = K - N + 1
    for i in range(N, K):
        if schedule[i] == 0 and schedule[i + 1] == 0:
            if i != N and schedule[i - 1] == 1:
                o += 1
            o += 1
    if o > 1:
        k = k - o
    return k

