    d = [10 ** 9] / N
    q = set(range(1, N))
    d[1] = 0

    def extract():
        mini = 10 ** 9
        o = 0
        for i in range(1, len(d)):
            if d[i] < mini and i in q:
                mini = d[i]
                o = i
        q.remove(o)
        return o
    while len(q) != 0:
        x = extract()
        for i in range(1, N):
            if i in q and cost_matrix[x][i] < d[i]:
                d[i] = cost_matrix[x][i]
    return sum(d[1:])

