def construct_permutation(N: int) -> list[int]:
    ans = []
    for (x, y) in zip(range(N, N // 2 - 1, -1), range(1, N // 2 + 1)):
        ans.append(x)
        ans.append(y)
    if N * 2:
        ans.append(N // 2 + 1)
    return list(reversed(ans))

