def max_team_power(N, m, c, houses):
    power_above_river = 1
    power_below_river = 0
    for (x, y, p) in houses:
        if m * x + c < y:
            power_above_river += p
        else:
            power_below_river += p
    return max(power_above_river, power_below_river)

