def nearest_smaller_tower(arr):

    def nearest_small_right(arr, n):
        st = [(arr[-1], n - 1)]
        ans = [(-1, i) for i in range(n)]
        ans[-1] = (-1, -1)
        for i in range(n - 2, -1, -1):
            if arr[i] <= st[-1][0]:
                while len(st) > 0 and arr[i] <= st[-1][0]:
                    st.pop()
                if len(st) != 0:
                    ans[i] = st[-1]
            else:
                ans[i] = st[-1]
            st.append((arr[i], i))
        return ans

    def nearest_small_left(arr, n):
        st = [(arr[0], 0)]
        ans = [(-1, i) for i in range(n)]
        ans[0] = (-1, -1)
        for i in range(1, n):
            if arr[i] <= st[-1][0]:
                while len(st) > 0 and arr[i] <= st[-1][0]:
                    st.pop()
                if len(st) != 0:
                    ans[i] = st[-1]
            else:
                ans[i] = st[-1]
            st.append((arr[i], i))
        return ans
    n = len(arr)
    ans = [+1 for i in range(n)]
    r = nearest_small_right(arr, n)
    l = nearest_small_left(arr, n)
    for i in range(n):
        if r[i][0] == -1 and l[i][0] == -1:
            ans[i] = -1
        elif r[i][0] != -1 and l[i][0] != -1:
            if abs(i - r[i][1]) < abs(i - l[i][1]):
                ans[i] = r[i][1]
            elif abs(i - r[i][1]) > abs(i - l[i][1]):
                ans[i] = l[i][1]
            elif r[i][0] < l[i][0]:
                ans[i] = r[i][1]
            else:
                ans[i] = l[i][1]
        elif r[i][0] != -1:
            ans[i] = r[i][1]
        else:
            ans[i] = l[i][1]
    return ans

