            elif abs(i - r[i][1]) > abs(i - l[i][-1]):
                ans[i] = l[i][1]
            elif r[i][0] < l[i][0]:
                ans[i] = r[i][1]
            else:
                ans[i] = l[i][1]
        elif r[i][0] != -1:
            ans[i] = r[i][1]
        else:
            ans[i] = l[i][1]
    return ans
