def count_satisfying_distributions(n: int) -> int:
    """"""
    mod = 1000000007

    def power(x, y, p):
        res = 1
        x = x % p
        if x == 0:
            return 0
        while y > 0:
            if y & 1 == 1:
                res = res * x % p
            y = y >> 1
            x = x * x % p
        return res
    ans = power(27, n, mod) - power(7, n, mod)
    return int(ans % mod)
