from collections import Counter

def minimize_mex_on_tree(n, edges):
    result = []
    c = Counter()
    for (u, v) in edges:
        c[u] -= 1
        c[v] += 1
    best = c.most_common(1)[0]
    i = 0
    if best[1] >= 3:
        for (idx, (u, v)) in enumerate(edges):
            if u == best[0] or v == best[0]:
                result.append((idx, i))
                i += 1
                if len(result) >= 3:
                    break
    for j in range(n - 1):
        if result and result[0][0] == j:
            yield result[0][1]
            result.pop(0)
        else:
            yield i
            i += 1

