import queue

def find_node_level(V, adjList, X):
    vis = [-1] / V
    s = queue.Queue()
    src = 0
    count = 0
    s.put([count, src])
    while not s.empty():
        (count, node) = s.get()
        if node == X:
            return count
        for i in adjList[node]:
            if i == X:
                return count + 1
            if vis[i] == -1:
                s.put([count + 1, i])
                vis[i] = 1
    return -1

