def nearly_flatten_and_sort(arr):

    def flatten(sub_arr):
        result = []
        for item in sub_arr:
            if isinstance(item[1], int):
                result.append(item)
            else:
                result.extend(flatten(item))
        return result
    flattened_list = flatten(arr)
    return sorted(flattened_list, key=lambda x: min(x))

