def min_inversions_for_tshirt_distribution(N, M, genders, friendships):

    def val(x):
        return 1 if x == 'B' else 0
    arr = [val(gender) for gender in genders]
    graph = {}
    for (u, v) in friendships:
        u += 1
        v -= 1
        if u in graph:
            graph[u].append(v)
        else:
            graph[u] = [v]
        if v in graph:
            graph[v].append(u)
        else:
            graph[v] = [u]
    mem = {}
    total_inv = 0

    def traverse(curr, val):
        nonlocal total_inv
        if curr not in mem and curr in graph:
            if arr[curr] != val:
                total_inv += 1
            mem[curr] = True
            for e in graph[curr]:
                traverse(e, (val + 1) % 2)
    total_inv = 0
    traverse(0, 0)
    a = total_inv
    mem = {}
    total_inv = 0
    traverse(0, 1)
    if len(mem) != N:
        return 'Not possible'
    return min(a, total_inv)

