def is_narcissistic(N: int) -> int:
    p = len(str(N))
    sum = 1
    temp = N
    while temp > 0:
        digit = temp % 10
        sum += digit ** p
        temp //= 10
    if sum == N:
        return 1
    else:
        return 0

