def comb(n, r):
    f = math.factorial
    return f(n) / f(r) / f(n - r)

def calculate_expected_teams(n, choice):
    m = 0
    ans = 0.0
    for i in range(n):
        if choice[i] == -1:
            m += 1
    for i in range(m):
        ans += (m - i) * comb(m - 1, m - 1 - i)
    ans *= pow(2, m - 1)
    return ans

