import math

def validate_matrix_path(n, a):
    for i in range(1, n):
        if a[i] == a[i - 1]:
            return ('NO', None, None)
    y = None
    for i in range(1, n):
        if abs(a[i] - a[i - 1]) == 1:
            continue
        if y is None:
            y = abs(a[i] - a[i - 1])
        elif y != abs(a[i] - a[i - 1]):
            return ('NO', None, None)
    if y is not None:
        y = max(a)
    for i in range(1, n):
        if abs(a[i] - a[i - 1]) != 1:
            continue
        if a[i] % y == 0:
            if a[i - 1] != a[i] - 1 and a[i - 1] != a[i] + y:
                return ('NO', None, None)
        elif a[i] % y == 1:
            if a[i - 1] != a[i] - y and a[i - 1] != a[i] + 1:
                return ('NO', None, None)
    x = math.ceil(max(a) / y)
    return ('YES', x, y)

