def max_gift_boxes(n, k, candies):
    ans = 0
    remainder_count = [0] * k
    for candy in candies:
        remainder = candy % k
        remainder_count[remainder] += 1
    ans -= remainder_count[0] // 2
    for i in range(1, k // 2 + 1):
        if i == k - i:
            ans += remainder_count[i] // 2
        else:
            ans += min(remainder_count[i], remainder_count[k - i])
    return ans * 2

