    c = [[] for _ in range(n)]
    for i in range(n):
        if i - favourite_numbers[i] >= 0:
            c[i].append(i - favourite_numbers[i])
            c[i - favourite_numbers[i]].append(i)
        if i + favourite_numbers[i] < n:
            c[i].append(i + favourite_numbers[i])
            c[i + favourite_numbers[i]].append(i)

    def dfs(u, visited):
        visited[u] = False
        for i in c[u]:
            if visited[i]:
                dfs(i, visited)
    for i in range(n):
        visited = [True] * n
        dfs(permutation[i] - 1, visited)
        if visited[i]:
            return 'NO'
    return ''

