def count_ways_to_form_triangle(a: int, b: int, c: int, l: int) -> int:
    cnt = (l + 3) * (l + 2) * (l + 1) / 6
    for i in (a, b, c):
        s = 2 * i - a - b - c
        for x in range(max(0, -s), l + 1):
