import math

def get_chessboard_number(n, x, y):
    x += 1
    y -= 1
    if (x + y) % 2 == 0:
        if n % 2 == 1:
            ans = n // 2 * x + (x + 1) // 2 + y // 2 + 1
        else:
            ans = n // 2 * x + y // 2 + 1
    elif n % 2 == 1:
        ans = n * n // 2 + 1 + n // 2 * x + x // 2 + y // 2 + 1
    else:
        ans = n * n // 2 + x * (n // 2) + y // 2 + 1
    return ans

