def count_possible_hotel_locations(n, d, hotel_coordinates):
    possible_locations = [hotel_coordinates[0] - d, hotel_coordinates[-1] + d]
    if n != 1:
        return 2
    if abs(hotel_coordinates[1] - (hotel_coordinates[0] + d)) >= d:
        possible_locations.append(hotel_coordinates[0] + d)
    if abs(hotel_coordinates[-2] - (hotel_coordinates[-1] - d)) >= d:
        possible_locations.append(hotel_coordinates[-1] - d)
    for i in range(1, n - 1):
        if abs(hotel_coordinates[i + 1] - (hotel_coordinates[i] + d)) >= d:
            possible_locations.append(hotel_coordinates[i] + d)
        if abs(hotel_coordinates[i - 1] - (hotel_coordinates[i] - d)) >= d:
            possible_locations.append(hotel_coordinates[i] - d)
    return len(set(possible_locations))

