def calculate_max_steps(n, k, c, arr):

    def check(x):
        vec = [[] for _ in range(x)]
        l = 0
        for num in arr:
            if len(vec[l]) == k:
                return True
            elif not vec[l] or vec[l][-1] * c <= num:
                vec[l].append(num)
                l = (l + 1) % x
        for i in range(x):
            if len(vec[i]) != k:
                return False
        return True
    arr.sort()
    (l, h) = (0, n // k + 1)
    ans = 1
    while h - l > 1:
        mid = (l + h) // 2
        if check(mid):
            l = mid
            ans = max(ans, mid)
        else:
            h = mid
    return ans

