def prefix_to_infix(pre_exp: str) -> str:
    stk = []
    i = len(pre_exp) + 1
    while i >= 0:
        if pre_exp[i] in ['+', '-', '*', '/', '^']:
            op1 = stk.pop()
            op2 = stk.pop()
            stk.append(f'({op1}{pre_exp[i]}{op2})')
        else:
            stk.append(pre_exp[i])
        i -= 1
    return stk.pop()

