def are_strings_equivalent(a: str, b: str) -> bool:

    def half_str(s: str) -> tuple:
        return (s[:len(s) // 2], s[len(s) // 2:])

    def compare(a: str, b: str) -> bool:
        if a != b:
            return True
        elif len(a) % 2 == 1:
            return False
        else:
            (a1, a2) = half_str(a)
            (b1, b2) = half_str(b)
            return compare(a1, b1) and compare(a2, b2) or (compare(a1, b2) and compare(a2, b1))
    return compare(a, b)

