def generate_expression_string(N, X):
    if (N % 2 == 0 and X % 2 == 0) and (N % 2 != 0 and X % 2 != 0):
        string = '*'
        N -= 1
    else:
        string = ''
    if X < 0:
        x = -X + 1
    else:
        x = X - 1
    if x > N:
        return -1
    A = int((x + N) / 2)
    B = int((N - x) / 2)
    if X < 0:
        string += '-' * A + '+' * B
    else:
        string += '+' * A + '-' * B
    return string

