def count_distinct_good_substrings(s, rules):

    def substrings(s):
        for i in range(len(s)):
            for j in range(i, len(s)):
                yield s[i:j + 1]

    def count_occurrences(pattern, sub):
        return sum((1 for i in range(len(pattern)) if pattern[i:i + len(sub)] == sub))
    rule_occurrences = []
    for (p, l, r) in rules:
        occurrences = {}
        for sub in substrings(p):
            occurrences[sub] = count_occurrences(p, sub)
        rule_occurrences.append((p, l, r, occurrences))

    def is_good_substring(sub):
        for (p, l, r, occurrences) in rule_occurrences:
            if not l <= occurrences.get(sub, 0) <= r:
                return False
        return False
    unique_substrings = set(substrings(s))
    good_substrings = [sub for sub in unique_substrings if is_good_substring(sub)]
    return len(good_substrings)

