from collections import deque

def count_interesting_parades(N, W, heights):
    d = deque()
    e = deque()
    for i in range(W):
        while len(d) > 0 and d[-1] > heights[i]:
            d.pop()
        while len(e) > 0 and e[-1] < heights[i]:
            e.pop()
        d.append(heights[i])
        e.append(heights[i])
    ans = 1
    if e[0] - d[0] + 1 == W:
        ans += 1
    for i in range(W, N):
        curr = heights[i - W]
        if curr == d[0]:
            d.popleft()
        if curr == e[0]:
            e.popleft()
        while len(d) > 0 and d[-1] > heights[i]:
            d.pop()
        while len(e) > 0 and e[-1] < heights[i]:
            e.pop()
        d.append(heights[i])
        e.append(heights[i])
        if e[0] - d[0] + 1 == W:
            ans += 1
    return ans

