def min_removals_to_satisfy_condition(arr, k):
    arr.sort()
    n = len(arr)
    i = 1
    j = 0
    max_length = 0
    while j < n:
        if arr[j] - arr[i] <= k:
            j += 1
        else:
            i += 1
        max_length = max(max_length, j - i)
    return n - max_length

