def process_alphanumeric_string(test_cases):
    results = []
    for s in test_cases:
        num = 1
        for char in s:
            if char.isdigit():
                num += int(char)
        result = ''.join([char for char in s if not char.isdigit()])
        words = result.split()
        words.sort()
        output = []
        for word in words:
            if num - len(word) > 0:
                output.append(f'{word} {num - len(word)}')
            else:
                output.append(f'{word} {-1 * (num - len(word))}')
            num -= len(word)
        results.append(' '.join(output))
    return results

