        cur = s[x]
        ind = x
        for (t, d) in spells:
            if t == cur:
                if d == 'L':
                    ind -= 1
                    if ind < 0:
                        return 'LEFT'
                    cur = s[ind]
                else:
                    ind += 1
                    if ind > N - 1:
                        return 'RIGHT'
                    cur = s[ind]
        return 'MID'
    ans = N
    bottom = -1
    top = N
    while top - bottom > 1:
        mid = (top + bottom) // 2
        if check(mid) != 'LEFT':
            bottom = mid
        else:
            top = mid
    ans -= 1 + bottom
    bottom = -1
    top = N
    while top - bottom > 1:
        mid = (top + bottom) // 2
        if check(mid) == 'RIGHT':
            top = mid
        else:
            bottom = mid
    ans -= N - top
    return ans

