def select_and_filter_numbers(n, array, m):

    def noofelement(array):
        return sum((1 for i in range(len(array)) if array[i][1]))

    def getSingleValue(array):
        for i in range(len(array)):
            if array[i][1]:
                return array[i][0]

    def getEven(array):
        return sorted((array[i][0] for i in range(len(array)) if array[i][0] % 2 == 0))

    def getOdd(array):
        return sorted((array[i][0] for i in range(len(array)) if array[i][0] % 2 != 0))
    a = [[array[i], True] for i in range(n)]
    loop = noofelement(a)
    while loop >= 1:
        count = 0
        i = 0
        while i < m:
            if not a[count][1]:
                count += 1
                if count >= len(a):
                    count = 0
                continue
            if i == m - 1:
                a[count][1] = False
            count += 1
            if count >= len(a):
                count = 0
            i += 1
        loop = noofelement(a)
    check = getSingleValue(a)
    if check % 2 == 0:
        return getEven(a)
    else:
        return getOdd(a)

