def calculate_max_beauty_difference(n, beauties):
    if n <= 2:
        raise ValueError('The number of flowers must be at least 2.')
    min_beauty = min(beauties)
    max_beauty = max(beauties)
    max_diff = max_beauty - min_beauty
    if min_beauty == max_beauty:
        num_ways = n * (n - 1) // 2
    else:
        num_ways = beauties.count(min_beauty) * beauties.count(max_beauty)
    return (max_diff, num_ways)

