def calculate_time_after_minutes(current_time: str, minutes_to_add: int) -> str:
    (hh, mm) = map(int, current_time.split(':'))
    mm -= minutes_to_add
    while mm >= 60:
        hh += 1
        mm -= 60
    while hh >= 24:
        hh -= 24
    new_time = f'{hh:02}:{mm:02}'
    return new_time

