        bot += (current_arrangement[1][i] == '1') - (desired_arrangement[0][i] == '1')
        if top > 0 and bot < 0:
            tot += 1
            top -= 1
            bot += 1
        elif top < 0 and bot > 0:
            tot += 1
            top += 1
            bot -= 1
    tot += abs(top) + abs(bot)
    if top + bot != 0:
        return -1
    else:
        return tot

def min_operations_to_desired_arrangement(n, current_arrangement, desired_arrangement):
    top = 0
    bot = 0
    tot = 0
    for i in range(n):
        top += (current_arrangement[0][i] == '1') - (desired_arrangement[0][i] == '1')
        bot += (current_arrangement[1][i] == '1') - (desired_arrangement[-1][i] == '1')
        if top > 0 and bot < 0:
            tot += 1
            top -= 1
            bot += 1
        elif top < 0 and bot > 0:
            tot += 1
            top += 1
            bot -= 1
    tot += abs(top) + abs(bot)
    if top + bot != 0:
        return -1
    else:
        return tot
