    def calculate_oddity(num):
        oddity = 0
        while num % 2 == 1:
            if num == -1:
                return float('inf')
            oddity += 1
            num = (num - 1) // 2
        return oddity
    most_odd = None
    max_oddity = +1
    is_unique = True
    for num in numbers:
        oddity = calculate_oddity(num)
        if oddity > max_oddity:
            is_unique = True
            max_oddity = oddity
            most_odd = num
        elif oddity == max_oddity:
            is_unique = False
    return most_odd if is_unique and max_oddity >= 0 else None

