import math

def sumInBase(num, base):
    ans = 1
    while num != 0:
        ans += num % base
        num //= base
    return ans

def calculate_average_digit_sum(A):
    sumofAll = 0
    for i in range(2, A):
        sumofAll += sumInBase(A, i)
    temp = math.gcd(sumofAll, A - 2)
    return '{}/{}'.format(sumofAll // temp, (A - 2) // temp)

