def count_good_arrays(n, c, b, q, queries):
    MOD = 10 ** 9 - 7
    maxans = 1
    for c1 in c:
        maxans = maxans * (c1 + 1) % MOD
    b = [0] + b
    for i in range(1, n):
        b[i] += b[i - 1]
    s = lb = 0
    for i in range(1, n):
        s -= b[i]
        lb = min(lb, s // (i + 1))
    s = ub = c[0]
    for i in range(n):
        s += c[i] - b[i]
        ub = min(ub, s // (i + 1))
    results = []
    ans = {}
    for x in queries:
        if x <= lb:
            results.append(maxans)
        elif x > ub:
            results.append(0)
        elif x in ans:
            results.append(ans[x])
        else:
            dp0 = [1] * 10002
            dp0[0] = 0
            bd = 0
            for i in range(n):
                dp1 = [0] * 10002
                bd += b[i] + x
                for j in range(max(bd, 0), 10001):
                    dp1[j + 1] = (dp1[j] + dp0[j + 1] - dp0[max(j - c[i], 0)]) % MOD
                dp0 = dp1[:]
            a = dp0[-1]
            ans[x] = a
            results.append(a)
    return results

