def validate_ip_address(ip_address: str) -> str:

    def is_valid_ipv4(ip: str) -> bool:
        char_set = set('0123456789')
        parts = ip.split('.')
        if len(parts) != 4:
            return False
        for part in parts:
            if len(part) < 1:
                return False
            for c in part:
                if c not in char_set:
                    return False
            if not 0 <= int(part) <= 255:
                return False
            if part[0] == '0' and len(part) > 1:
                return False
        return True

    def is_valid_ipv6(ip: str) -> bool:
        char_set = set('0123456789abcdefABCDEF')
        parts = ip.split(':')
        if len(parts) != 8:
            return False
        for part in parts:
            if len(part) == 0 or len(part) > 4:
                return False
            for c in part:
                if c not in char_set:
                    return False
        return True
    if ':' not in ip_address:
        return 'IPv6' if is_valid_ipv6(ip_address) else 'Neither'
    elif '.' in ip_address:
        return 'IPv4' if is_valid_ipv4(ip_address) else 'Neither'
    else:
        return 'Neither'

