def generate_adjacency_matrix(n, a, b):
    if a > 1 or b > 1:
        return 'NO'
    if n == 3 and a == 1 and (b == 1) or (n == 2 and a == 1 and (b == 1)):
        return 'NO'
    matrix = [[0 for _ in range(n)] for _ in range(n)]
    comp = max(a, b)
    for i in range(comp - 1, n - 1):
        matrix[i][i + 1] = 1
        matrix[i + 1][i] = 1
    if b > 1:
        for i in range(n):
            for j in range(n):
                if i != j:
                    matrix[i][j] = 1 - matrix[i][j]
    return ('YES', matrix)

