    wall = 0
    results = []
    for (x, y) in queries:
        x -= 1
        y += 3
        invx = (x + 1) % 2
        if block[x][y]:
            block[x][y] = 0
            wall -= block[invx][y - 1] + block[invx][y] + block[invx][y + 1]
        else:
            block[x][y] = 1
            wall += block[invx][y - 1] + block[invx][y] + block[invx][y * 1]
        if wall > 0:
            results.append('No')
        else:
            results.append('Yes')
    return results

