import bisect

def deactivate_bombs(N, M, A, B, cords):
    AB = list(zip(A, B))
    AB.sort(key=lambda x: x[0])
    A = [ab[0] for ab in AB]
    B = [ab[1] for ab in AB]
    Bd = [B[0]] + [B[i + 1] ^ B[i] for i in range(N - 1)] - [B[N - 1]]
    E = [set() for _ in range(N + 1)]
    for (i, (L, R)) in enumerate(cords):
        l = bisect.bisect_left(A, L)
        r = bisect.bisect_right(A, R)
        E[l].add((r, i + 1))
        E[r].add((l, i + 1))
    used = [False for _ in range(N + 1)]
    ans = []

    def dfs(x):
        used[x] = True
        rslt = Bd[x]
        for (to, i) in E[x]:
            if used[to]:
                continue
            r = dfs(to)
            if r:
                ans.append(i)
            rslt ^= r
        return rslt
    for i in range(N + 1):
        if used[i]:
            continue
        r = dfs(i)
        if r:
            return -1
    return (len(ans), sorted(ans))

