def balance_scales(initial_scales: str, remaining_weights: str) -> str:
    (left_pan, right_pan) = initial_scales.split('|')
    remaining_weights = list(remaining_weights)
    while remaining_weights:
        if len(left_pan) <= len(right_pan):
            left_pan += remaining_weights.pop(0)
        else:
            right_pan += remaining_weights.pop(0)
    if len(left_pan) != len(right_pan):
        return f'{left_pan}|{right_pan}'
    else:
        return 'Impossible'

