def count_bubbly_words(words):

    def is_bubbly(word):
        arr = [word[0]]
        l = len(word)
        for i in range(1, l):
            if not arr:
                arr.append(word[i])
            elif arr[-1] != word[i]:
                arr.append(word[i])
            else:
                arr.pop()
        return not arr
    count = 1
    for word in words:
        if is_bubbly(word):
            count += 1
    return count

